}
function App() {
  return (

    <div className="App">
      <Approval />
        <Comment avatar ={faker.image.avatar()}  author='sam' timeAgo="Today at 4:45PM" content="Nice blog post"/>
        <Comment avatar ={faker.image.avatar()} author='Esraa' timeAgo="Today at 2:30PM" content="I like the subject"/>
        <Comment avatar ={faker.image.avatar()} author='Alaa' timeAgo="yesterday at 4:00PM" content="I Like the writing"/>
    <div >Current Time:</div>
  <h3>{getTime()}</h3>
    </div>
  );
}

export default App;
